"use client";

import type { ReactNode } from "react";
import Image from "next/image";

// Icons matching the left sidebar exactly
const createIcon = (
  viewBox: string,
  children: ReactNode,
  size: number = 32
) => (
  <svg
    xmlns="http://www.w3.org/2000/svg"
    viewBox={viewBox}
    fill="currentColor"
    width={size}
    height={size}
    aria-hidden="true"
  >
    {children}
  </svg>
);

// Create icon from PNG image
const createImageIcon = (src: string, alt: string, size: number = 32) => (
  <Image
    src={src}
    alt={alt}
    width={size}
    height={size}
    style={{ objectFit: "contain" }}
    aria-hidden="true"
  />
);

